import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { useState } from 'react';

export default function Login()  {
    const [form, setForm ] = useState({
        email: "",
        senha:"",
    })

    const controleMudanca = (evento) => {
        //console.log(evento.target.value);

        setForm({
            ...form,
            [evento.target.id]: evento.target.value
        })
    }




    return (
        <div>
            <h3 className="my-5">Login</h3>
            <Container className="text-center">
                <Form className="col-lg-6 col-md-6 mx-auto">
                    <Form.Group as={Row} controlId="formHorizontalEmail">
                        <Form.Label column sm={2}>
                        E-mail
                        </Form.Label>
                        <Col sm={10}>
                        <Form.Control onChange={controleMudanca} type="email" name="email" placeholder="E-mail" />
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} controlId="formHorizontalPassword">
                        <Form.Label column sm={2}>
                        Senha
                        </Form.Label>
                        <Col sm={10}>
                        <Form.Control onChange={controleMudanca} type="password" name="senha" placeholder="6 dígitos" />
                        </Col>
                    </Form.Group>
                    <Button className="mt-3" variant="success" type="submit" block>Entrar</Button>
                    <p className="forgot-password mt-3">
                        Esqueci minha <a href="#">senha</a>
                    </p>
                    <p className="cadastrar mt-3">
                        Ainda não possui cadastro?
                        <a href="/cadastro"> Clique aqui para se cadastrar</a>
                    </p>
                </Form>
            </Container>
        </div>
    );
}
