import Produto from '../Componentes/Produto';
import { CardColumns, Row, Col, ListGroup } from 'react-bootstrap';
import { useState, useEffect } from 'react';

export default function Produtos()  {
    const [ produtos, setProdutos] = useState([]);

    useEffect( () => {
        async function pegaProdutos(){
            const url = "http://localhost/Recode/7_aulas_React/php/api/produtos.php/";
            const resposta = await fetch(url);
            const dados = await resposta.json();
            setProdutos(dados);   
        }  
        pegaProdutos();    
    }, []);

    const [categoria, setCategoria] = useState('');


    const filtraCategoria = (evento) => {
        setCategoria(evento.target.value); 
    }
    console.log(categoria); 



    return (
        <div>
            <h3 className="my-5">Produtos</h3>
            <Row>
                <Col sm={2} className="coluna-categorias">
                    <ListGroup variant="flush" className= "categorias">
                        <ListGroup.Item action variant="light" value="" onClick={filtraCategoria}>Mostrar tudo</ListGroup.Item>
                        <ListGroup.Item action variant="light" value="geladeira" onClick={filtraCategoria}>Geladeiras</ListGroup.Item>
                        <ListGroup.Item action variant="light" value="fogao" onClick={filtraCategoria}>Fog√µes</ListGroup.Item>
                        <ListGroup.Item action variant="light" value="microondas" onClick={filtraCategoria}>Micro-ondas</ListGroup.Item>
                        <ListGroup.Item action variant="light" value="lavaLoucas" onClick={filtraCategoria}>Lavadoras de roupas</ListGroup.Item>
                        <ListGroup.Item action variant="light" value="lavadoraRoupas" onClick={filtraCategoria}>Lava-lou√ßas</ListGroup.Item>
                    </ListGroup>
                </Col>
                <Col sm={10} className="coluna-produtos">
                    <CardColumns className="text-center">
                        {produtos && produtos.map(item => 
                            < Produto categoria= {item.categoria} 
                            id={item.idProd} imagem= {item.imagem} descricao= {item.descricao} preco= {item.preco} 
                            precoVenda= {item.precoVenda} />
                        )}
                    </CardColumns>
                </Col>
            </Row>
        </div>
    );
}
