import Produto from '../Componentes/Produto';
import { CardColumns, Row, Col,ListGroup, Card, Button } from 'react-bootstrap';
import { useState, useEffect } from 'react';


export default function Produtos()  {
    const [ produtos, setProdutos] = useState([]);
    const [categoria, setCategoria] = useState('');



    const filtraCategoria = (evento) => {
        setCategoria({categoria: ""});
        setCategoria({categoria: evento.target.value});
        console.log("clicou!");
        console.log(categoria);

    }

    useEffect( () => {
        async function pegaProdutos(){
            const url = "http://localhost/Recode/7_aulas_React/fullstackeletro/php/api/produtos.php/";
            const resposta = await fetch(url);
            const dados = await resposta.json();
            setProdutos(dados);    
        }  
        pegaProdutos();    
    }, []);

    return (
        <div>
            <h3 className="my-5">Produtos</h3>
            <Row>
                <Col sm={2} className="coluna-categorias">
                <ListGroup variant="flush" className= "categoria">
                    <ListGroup.Item action value="" variant="light" onClick={filtraCategoria}>Mostrar tudo</ListGroup.Item>
                    <ListGroup.Item action value="geladeira" ariant="light" onClick={filtraCategoria}>Geladeiras</ListGroup.Item>
                    <ListGroup.Item action value="fogao" variant="light" onClick={filtraCategoria}>Fogões</ListGroup.Item>
                    <ListGroup.Item action value="microondas" variant="light" onClick={filtraCategoria}>Micro-ondas</ListGroup.Item>
                    <ListGroup.Item action value="lavaLoucas" variant="light" onClick={filtraCategoria}>Lavadoras de roupas</ListGroup.Item>
                    <ListGroup.Item action value="lavadoraRoupas" variant="light" onClick={filtraCategoria}>Lava-louças</ListGroup.Item>
                </ListGroup>
                </Col>
                <Col sm={10} className="coluna-produtos">
                    <CardColumns className="text-center">
                        {produtos && produtos.map(({categoria, imagem, descricao, preco, precoVenda }) => (
                            <Card className={categoria}>
                                <Card.Img src={require(`../Componentes/imgs/${imagem}`).default} variant="top" alt ="Imagem do produto" />
                                <Card.Body>
                                    <Card.Title className="nome-produto">{descricao}</Card.Title>
                                    <Card.Text className="preco-antigo">R$ {preco}</Card.Text>
                                    <Card.Text className="preco-novo">R$ {precoVenda}</Card.Text>
                                    <Button variant="success" type="button" href="#" block>Confira</Button>
                                </Card.Body>
                            </Card>
                            )
                        )}
                    </CardColumns>
                </Col>
            </Row>
        </div>
    );
}
