import Produto from '../Componentes/Produto';
import { CardColumns, Row, Col,ListGroup } from 'react-bootstrap';
import { useState, useEffect } from 'react';


export default function Produtos()  {
    const [ produtos, setProdutos] = useState([]);
    const [categoria, setCategoria] = useState('');

    useEffect(async () => {
        const url = "http://localhost/Recode/7_aulas_React/fullstackeletro/php/api/produtos.php/";
        const resposta = await fetch(url);
        const dados = await resposta.json();


        setProdutos(dados);          
    }, []);

    const filtraCategoria = (evento) => {
        console.log("clicou!")

    }



    return (
        <div>
            <h3 className="my-5">Produtos</h3>
            <Row>
                <Col sm={2} className="coluna-categorias">
                <ListGroup variant="flush" className= "categorias">
                    <ListGroup.Item action variant="light" onClick={filtraCategoria}>Mostrar tudo</ListGroup.Item>
                    <ListGroup.Item action variant="light" onClick={filtraCategoria}>Geladeiras</ListGroup.Item>
                    <ListGroup.Item action variant="light" onClick={filtraCategoria}>Fogões</ListGroup.Item>
                    <ListGroup.Item action variant="light" onClick={filtraCategoria}>Micro-ondas</ListGroup.Item>
                    <ListGroup.Item action variant="light" onClick={filtraCategoria}>Lavadoras de roupas</ListGroup.Item>
                    <ListGroup.Item action variant="light" onClick={filtraCategoria}>Lava-louças</ListGroup.Item>
                </ListGroup>
                </Col>
                <Col sm={10} className="coluna-produtos">
                    <CardColumns className="text-center">
                        {produtos && produtos.map(item => < Produto categoria= {item.categoria} 
                        imagem= {item.imagem} descricao= {item.descricao} preco= {item.preco} 
                        precoVenda= {item.precoVenda} />)}
                    </CardColumns>
                </Col>
            </Row>
        </div>
    );
}
