import produtos from './produtos';

//aqui, além de trazer os dados do arquivo produtos, precisa ser criado o campo quantidade
const initialState = produtos.map(produto => ({...produto, quantidade: 0}));

export default function carrinhoReducer(state = initialState, action) {
    switch(action.type) {
        case "ADICIONAR_NO_CARRINHO":
            return state;

        case "REMOVER_DO_CARRINHO":
            return state;

        case "LIMPAR_O_CARRINHO":
            return state;

        case "":
            return state;
    }
}
