import Produto from '../Componentes/Produto';
import Categorias from '../Componentes/Categorias';
import { CardColumns, Row, Col } from 'react-bootstrap';
import { useState, useEffect } from 'react';

export default function Produtos()  {
    const [ produtos, setProdutos] = useState([]);

    useEffect(async () => {
        const url = "http://localhost/Recode/7_aulas_React/fullstackeletro/php/api/produtos.php/";
        const resposta = await fetch(url);
        const dados = await resposta.json();


        setProdutos(dados);          
    }, []);


    return (
        <div>
            <h1 className="my-5">Produtos</h1>
            <Row>
                <Col sm={2} className="area-categorias">
                    <Categorias />
                </Col>
                <Col sm={10} className="area-produtos">
                    <CardColumns className="text-center">
                        {produtos && produtos.map(item => < Produto categoria= {item.categoria} 
                        imagem= {item.imagem} descricao= {item.descricao} preco= {item.preco.toLocaleString('pt-br', {minimumFractionDigits: 2})} 
                        precoVenda= {item.precoVenda} />)}
                    </CardColumns>
                </Col>
            </Row>
        </div>
    );
}
